import { Button } from "react-bootstrap";
import { store } from '../store/store';
import { addItem }from '../actions/cart';
import { connect } from "react-redux";


const BuyItem = (props) => {
    // const handleAdd = () => {
    //     store.dispatch(addItem('a'));
    // }

    return (
        <div className="d-flex flex-column m-5">
            <a className="mb-3" href={props.url}>
                <img src={props.imgurl} alt="Amazon.com"></img>
            </a>
            <div className="d-flex flex-column align-items-center justify-content-center">
                <div>{props.price}</div>
                <Button onClick={props.reduxAddItem('a')} variant="outline-info">Add to Cart</Button>
            </div>

        </div>
    )
}

const mapDispatchToProps = (dispatch) => {
    return {
        reduxAddItem: (item) => dispatch(addItem(item))
    };
};

export default connect(null, mapDispatchToProps)(BuyItem);
