const initialState = {
    currentUser: null,
};

const authReducer = (state=initialState, action) => {
    console.log(action);
    switch(action.type) {
        case 'LOGIN':
            return {
                ...state, 
                loggedIn: action.trueFalse
            }
    };
};

export default authReducer;
