import { Form, FormGroup, FormLabel, FormControl, FormText, Button } from "react-bootstrap";
import Layout from '../components/layout';
import React from "react";
import axios from 'axios';


class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            username: '',
            password: ''
        }
    }

    handleSubmit = (event) => {
        const url = 'http://localhost:3000/';
        console.log(this.state);
        event.preventDefault();
        axios.post(url+'/login',
            JSON.stringify(this.state)
        )
        .then((response) => {
            console.log(`Response: ${response}`);
        }, (error) => {
            console.log(error);
        });
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    render() {
        return (
        <Layout>
            {(parent) => 
                <div className="mt-5">
                    <Form className="container card card-body rounded col-md-6 m-auto d-flex flex-column" onSubmit={this.handleSubmit}>
                        <h1>Login Here</h1> 
                        <FormGroup controlId="formEmail">
                            <FormLabel>Email Address</FormLabel>
                            <FormControl type="email" name="username" onChange={this.handleChange} placeholder="Email"/>
                            <FormText className="text-muted">We will never share your email.</FormText>
                        </FormGroup>
                        <FormGroup controlId="formPassword">
                            <FormLabel>Password</FormLabel>
                            <FormControl type="password" name="password" onChange={this.handleChange} placeholder="Password"/>
                            <FormText className="text-muted"></FormText>
                        </FormGroup>
                        <FormGroup className="d-flex justify-content-between align-items-start">
                            <Button variant="outline-info" type="submit">Login</Button>
                            <FormGroup className="d-flex flex-column align-items-end" controlId="register">
                                <Button variant="outline-info" href="/register">Register</Button>
                                <FormText className="text-muted">New? Register for an Account</FormText>
                            </FormGroup>
                        </FormGroup>
                    </Form>
                </div>
            }
        </Layout>
        )
    }

}

export default Login;
