import { useReducer } from "react";

const initialState = [];

const itemReducer = (state=initialState, action) => {
    console.log(action);
    switch(action.type) {
        case 'ADD_ITEM': {
            return [...state, action.item];
        }
        default: {
            return state;
        }
    }
};

export default itemReducer;
