import { useReducer } from "react";
import {REHYDRATE, PERSIST} from 'redux-persist'

const initialState = [];

const itemReducer = (state=initialState, action) => {
    console.log("Reducer item to add: ",action);
    console.log("Current State: ", state);
    switch(action.type) {
        case REHYDRATE:
            console.log("REHYDRATE CASE");
            return [];
        case PERSIST: 
            console.log("PERSIST CASE");
            return state;
        case 'ADD_ITEM': {
            return [...state, action.item];
        }
        default: {
            console.log("Default reduce: ", state);
            return [...state];
        }
    }
};

export default itemReducer;
