import { Button } from "react-bootstrap";

const cartItem = ({ name, url, price, imgurl, index }) => {
    return (
        <div>
            <div className="d-flex flex-column m-5 card card-body">
                <button type="button" className="close align-self-end" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
                <a className="mb-3" href={url}>
                    <img src={imgurl} alt="Amazon.com"></img>
                </a>
                <h3>{price}</h3>
            </div>
        </div>
    )
}

export default cartItem;
