import Head from 'next/head';
import { Navbar, NavLink, Form, InputGroup, FormControl, Nav, Button, Row, Col, Container, Dropdown, DropdownButton } from 'react-bootstrap'
import { InputGroupAppend } from 'react-bootstrap/InputGroup';
import React, { useState } from 'react';
import axios from 'axios';
import DropdownItem from 'react-bootstrap/esm/DropdownItem';
import DropdownMenu from 'react-bootstrap/esm/DropdownMenu';

const Layout = (props) => {
    const [searchField, updateSearchField] = useState("");
    const [searchResults, updateSearchResults] = useState([]);
    const url = 'http://localhost:3000/';

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post(url+'search',
            {
                searchField: searchField
            }
        )
        .then((response) => {
            console.log(`Response: ${response}`);
            updateSearchResults([response.data.searchResults]);

        }, (error) => {
            console.log(error);
        });
    }

    const handleChange = input => (event) => {
        input(event.target.value);
    }


    return (
        <div>
            <Head>
                <link
                    rel="stylesheet"
                    href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css"
                    integrity="sha384-9aIt2nRpC12Uk9gS9baDl411NQApFmC26EwAOH8WgZl5MYYxFfc+NcPb1dKGj7Sk"
                    crossOrigin="anonymous"
                />
                <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"/>
            </Head>

            <Navbar bg="dark" className="container-fluid">
                <Col lg={3} xs={1}></Col>
                <Col lg={6} xs={8}>
                    <Form onSubmit={handleSubmit}>
                        <InputGroup >
                            <FormControl onChange={handleChange(updateSearchField)}/>
                            <InputGroup.Append>
                                <Button type="submit" id="basic-addon1" variant="outline-info"><i className="fa fa-search " aria-hidden="true"></i></Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </Form> 
                </Col>
                <Col lg={3} xs={3}>
                    {/* <Nav className="d-flex space-between justify-content-end">
                        <Button className="mr-1" variant="outline-info" href="/">Home</Button>
                        <Button className="ml-1" variant="outline-info" href="/login">Login</Button>
                    </Nav> */}
                    <DropdownButton size="sm" variant="outline-info" id="dropdown-item-button" title="Action">
                        <div className="dropdown-menu dropdown-menu-right">
                            <DropdownItem as="button">
                                <Button className="mr-1" variant="outline-info" href="/">Home</Button>
                            </DropdownItem>
                            <DropdownItem as="button">   
                                <Button className="ml-1" variant="outline-info" href="/login">Login</Button>
                            </DropdownItem>
                        </div>
                    </DropdownButton>
                </Col>
            </Navbar>
            {props.children(searchResults, handleChange)}
        </div>


    )
}

const containerFluid = {
    // max-width: '100%',
    // display: 'flex',
    // justifyContent: 'center'
};

export default Layout;
