import { Alert } from "react-bootstrap";


const Flash = (props) => {
    return (
        <div>
            { props.message && (
                <Alert variant="danger">
                    <p style={{marginBottom: 0}}>{props.message}</p>
                </Alert>
            )
            }
        </div>
    )
}

export default Flash;
