import Head from 'next/head';
import { Navbar, NavLink, Form, InputGroup, FormControl, Nav, Button, Row, Col, Container } from 'react-bootstrap'
import { InputGroupAppend } from 'react-bootstrap/InputGroup';
import React, { useState } from 'react';

const Layout = (props) => {
    const [searchField, updateSearchField] = useState("")

    const handleSubmit = (event) => {
        console.log(event);
    }


    return (
        <div>
            <Head>
                <link
                    rel="stylesheet"
                    href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.0/css/bootstrap.min.css"
                    integrity="sha384-9aIt2nRpC12Uk9gS9baDl411NQApFmC26EwAOH8WgZl5MYYxFfc+NcPb1dKGj7Sk"
                    crossOrigin="anonymous"
                />
                <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"/>


            </Head>


            <Navbar bg="dark" className="container-fluid mr-auto">
                <Col md={3}></Col>
                <Col md={6}>
                    <Form onSubmit={handleSubmit}>
                        <InputGroup>
                            <FormControl value={searchField}/>
                            <InputGroup.Append>
                                <Button id="basic-addon1" variant="outline-info"><i className="fa fa-search " aria-hidden="true"></i></Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </Form> 
                </Col>
                <Col md={3} >
                    <Nav className="space-between justify-content-end">
                        <Button className="mx-1" variant="outline-info" href="/">Home</Button>
                        <Button className="mx-1" variant="outline-info" href="/login">Login</Button>
                    </Nav>
                </Col>
            </Navbar>
            {props.children}
            {/* <style jsx>{`
                .container
            `}</style> */}
        </div>


    )
}

const containerFluid = {
    // max-width: '100%',
    // display: 'flex',
    // justifyContent: 'center'
};

export default Layout;
